CREATE TABLE `candidates` ( `name` TEXT, `ed_code` INTEGER, `party` TEXT, `nomination_dt` TEXT, `cabinet_position` TEXT, `email` TEXT, `phone` TEXT, `photo` TEXT, `donate` TEXT, `website` TEXT, `facebook` TEXT, `instagram` TEXT, `twitter` TEXT, `bio` TEXT, PRIMARY KEY(`name`), FOREIGN KEY (ed_code) REFERENCES electoral_districts(ed_code), FOREIGN KEY (party) REFERENCES parties(title) )